svg.script:

var card = document.getElementById('card')
var card_count = 4
var xmlns = 'http://www.w3.org/2000/svg'
var xlinkns = 'http://www.w3.org/1999/xlink'
if (false) {
    for (var a=0;a<360;a+=5) {
        var catbox = document.createElementNS(xmlns, 'g')
        catbox.setAttributeNS(null,'transform',` scale(2)  rotate(${ 2 * a } 200 300) `)
        var cat = document.createElementNS(xmlns, 'image')
        cat.setAttributeNS(xlinkns,'href',`ivy.png?nobby=${card_count}`);
        cat.setAttributeNS(null, 'width','20%')
        cat.setAttributeNS(null, 'height','20%')
        cat.setAttributeNS(null, 'x',`${ a*3 }`)
        cat.setAttributeNS(null, 'y',`${ a*2 }`)
        catbox.appendChild(cat)
        card.appendChild(catbox)
    }
}
if (true) {
    for (var i=0;i<200;i++) {
        var catbox = document.createElementNS(xmlns, 'g')
        catbox.setAttributeNS(null,'transform',` scale( ${Math.random()*2 + .5 } )  rotate(${ Math.random()*100 } 300 300) `)
        var cat = document.createElementNS(xmlns, 'image')
        cat.setAttributeNS(xlinkns,'href',`ivy.png?nobby=${card_count}`);
        cat.setAttributeNS(null, 'width','20%')
        cat.setAttributeNS(null, 'height','20%')
        cat.setAttributeNS(null, 'x',`${ Math.random()*140 }`)
        cat.setAttributeNS(null, 'y',`${ Math.random()*600 }`)
        catbox.appendChild(cat)
        card.appendChild(catbox)
    }
}
