svg.script:
///twitch();

var pos = 0
var spacing_x = 40
var spacing_y = 40
var num_cards = 3
var cards_per_page = num_cards * num_cards

var deck_offset_x = 20
var deck_offset_y = 20
var deck_scale = 1 / (num_cards+1)

var deck = document.getElementById('deck')
deck.setAttribute('transform','scale('+deck_scale+') translate('+deck_offset_x+' '+deck_offset_y+')')

var main_panel = document.getElementById('main_panel')
main_panel.setAttribute('transform','scale(.95)')

var line

line = document.getElementById('line0')
line.setAttribute('x1', 1*(1000 + spacing_x) - spacing_x/2)
line.setAttribute('x2', 1*(1000 + spacing_x) - spacing_x/2)
line.setAttribute('y1','0')
line.setAttribute('y2',(1410 + spacing_y/2)* num_cards + deck_offset_y/2 )

line = document.getElementById('line1')
line.setAttribute('x1', 2*(1000  + spacing_x) - spacing_x/2)
line.setAttribute('x2', 2*(1000  + spacing_x) - spacing_x/2)
line.setAttribute('y1','0')
line.setAttribute('y2',(1410 + spacing_y/2)* num_cards + deck_offset_y/2 )

line = document.getElementById('line2')
line.setAttribute('y1', 1*(1410 + spacing_y) - spacing_y/2)
line.setAttribute('y2', 1*(1410 + spacing_y) - spacing_y/2)
line.setAttribute('x1','0')
line.setAttribute('x2',(1000 + spacing_x/2)* num_cards  + deck_offset_x/2  )

line = document.getElementById('line3')
line.setAttribute('y1', 2*(1410 + spacing_y) - spacing_y/2)
line.setAttribute('y2', 2*(1410 + spacing_y) - spacing_y/2)
line.setAttribute('x1','0')
line.setAttribute('x2',(1000 + spacing_x/2 )* num_cards + deck_offset_x/2 )



//main card object (deleted at end of script) 

//change main card and clone and put in deck group
function createcard() {
    //suit(pattern)
    //value(val)
    var card = document.getElementById('card')
    card.style.visibility = 'visible'
    var hello = card.cloneNode(true)

    hello.id = (new Date).getTime()
    hello.setAttribute('transform','translate('+((spacing_x+1000)*(pos % num_cards))+' '+((spacing_y+1410)*Math.floor(pos/num_cards))+')');
    console.log(hello)
    var deck = document.getElementById('deck')
    deck.appendChild(hello)
    card.style.visibility = 'hidden'
    pos++
}

// page number unless comes from parameter
page_number=0

//count of cards printed
card_count=0

//get the page number from parameter
if (window.location.search) {
    re = /\?page=(\d+)/;
    page_number =Number( window.location.search.match(re)[1] )
    //alert(page_number)
} else {
    // no page number, run test page        
    v = []
    //all numbers and extra each suit
    for (var nn=0;nn<8;nn++) v.push(String(nn))
}

//print cards only to fill page
for (var vv=0;vv<v.length;vv++) {
    if (card_count>=page_number*cards_per_page && card_count<(page_number+1)*cards_per_page) createcard()
    card_count++
}


document.getElementsByTagName('svg')[0].removeChild(document.getElementById('card'))
console.log(document.getElementsByTagName('svg')[0])
